(function(){"use strict";document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll('[data-shortcode="input"]').forEach(t=>{const a=t.getAttribute("data-name")||"",n=t.getAttribute("data-placeholder")||"",e=document.createElement("input");e.type="text",e.name=a,e.placeholder=n,t.replaceWith(e)});const c=[],d=document.createTreeWalker(document.body,NodeFilter.SHOW_TEXT,null);let o;for(;o=d.nextNode();)o.nodeValue?.includes("{{")&&c.push({node:o,template:o.nodeValue});const l=()=>{const t=document.querySelectorAll("input[name]"),a={};t.forEach(n=>{a[n.name]=n.value}),c.forEach(n=>{let e=n.template;Object.entries(a).forEach(([r,u])=>{const s=new RegExp(`{{${r}}}`,"g");e=e.replace(s,u)}),n.node.nodeValue=e})};document.querySelectorAll("input[name]").forEach(t=>{t.addEventListener("input",l)}),l()})})();
